import { GET_ALL_CHARACTERS } from "./actions"


const initialState = {
    characters: [],
    details:{}
}

export default function rootReducer(state = initialState,action){
    switch(actions.type){
        case GET_ALL_CHARACTERS:
            return{
                ...state,
                characters: actions.payload
            }
        default: return {...state}
    }
}
